module.exports = strip;

function strip(html, options){
  var output = html.replace(/<br\s?\/?>/gi, '\n');

  if(options && options.formatLinks){
      output = output
      .replace(/<a[^<>]+href\=\"([^"]+)\"[^<>]*>(http|www)[^<>]+<\/a>/g, '$1')
      .replace(/<a[^<>]+href\=\"([^"]+)\"[^<>]*>([^<>]+)<\/a>/g, '[$2]($1)');
  }

  output = output.replace(/<[^<]+>/g, '').trim();

  return output;
}
